'              ceiling_robust=huge(0)', &
'           else', &
'              ierr_local=2', &
'              ceiling_robust=-huge(0)-1', &
'              write(message_local,*)''<ERROR>X='',x,'' <='',anint(real(-huge(0)-1))', &
'           endif', &
'        else', &
'           ! used to use a computed goto to do this!', &
'           ceiling_robust = int(x)', &
'           if (x > 0.0) then', &
