case('159','merge')

textblock=[character(len=256) :: &
'', &
'merge(3fortran)                                               merge(3fortran)', &
'', &
'NAME', &
'  MERGE(3) - [ARRAY:CONSTRUCTION] Merge variables', &
'', &
'SYNOPSIS', &
'  result = merge(tsource, fsource, mask)', &
'', &
