case('174','not')

textblock=[character(len=256) :: &
'', &
'not(3fortran)                                                   not(3fortran)', &
'', &
'NAME', &
'  NOT(3) - [BIT:LOGICAL] Logical negation; flips all bits in an integer', &
'', &
'SYNOPSIS', &
