'            ! >>>', &
'            write(*,brackets)kludge', &
'', &
'         endblock', &
'', &
'         ! Execution of BOUNDS form (return position of tokens)', &
'', &
'         block', &
'            character (len=:), allocatable :: string', &
'            character (len=*),parameter :: set = " ,"', &
'            integer, allocatable        :: first(:), last(:)', &
'            write(*,gen)repeat(''1234567890'',6)', &
