'        11, 22, 33, 44, -1055  &', &
'       ],shape(values),order=[2,1])', &
'', &
'        array=values', &
'        call print_matrix_int(''array:'',array)', &
'        array=transpose(array)', &
'        call print_matrix_int(''array transposed:'',array)', &
'        array=transpose(array)', &
'        call print_matrix_int(''transposed transpose:'',array)', &
'', &
'      contains', &
'', &
