'        ! make buffer to write integer into', &
'        biggest=''          ''', &
'        ! find how many characters to use for integers', &
'        write(biggest,''(i0)'')ceiling(log10(max(1.0,real(maxval(abs(arr))))))+2', &
'        ! use this format to write a row', &
'        biggest=''("  [",*(i''//trim(biggest)//'':,","))''', &
