character(len=*),intent(in),optional :: fmt
character(len=:),allocatable         :: outstr                  ! output string to generate
character(len=80)                    :: string
   if(present(fmt))then
      call value_to_string(dvalue,string,fmt=fmt)
   else
      call value_to_string(dvalue,string)
   endif
   outstr=trim(string)
end function d2s
