!!
!!  > STRING: +10            :VALUE: 10
!!  > STRING:     -3         :VALUE: -3
!!  > STRING:                :VALUE: 0
!!  > STRING: 1 2 1 2 1 . 0  :VALUE: 1
!!  > STRING: WHAT?          :VALUE: 0
!!
!!##AUTHOR
!!    John S. Urban
!!
!!##LICENSE
!!    Public Domain
character(len=*), intent(in) :: string
character(len=1)             :: c
integer                      :: i
integer                      :: j
integer                      :: ilen
logical                      :: neg

   val = 0
   neg=.false.
   i=0
   c=' '

   ilen=len(string)
   do i=1, ilen                               ! Pass over any leading spaces
      c = string(i:i)
      if (c  /=  ' ') exit
   enddo

   if (c  ==  '-') then                       ! check for +- as first digit
      neg = .true.
      i = i + 1
   elseif (c  ==  '+') then
      neg = .false.
      i = i + 1
   endif

   do j=i,ilen                                ! Continue as long as its a digit ...
      c = string(j:j)
      if (lge(c, '0') .and. lle(c, '9')) then
         val = 10*val + ichar(c)-48           ! Shift number over and add new digit
      else
         exit
      endif
   enddo

   if (neg) val = -val                        ! Negate the result if necessary

end function atol
