!!       ! copy stdin to stdout as a stream one byte at a time
!!       do while (getchar(byte).ge.0)
!!          istat=putchar(byte)
!!       enddo
!!    end program demo_getchar
!!
!!##AUTHOR
!!    John S. Urban
!!##LICENSE
!!    Public Domain
function getchar(ch)
character(len=1),intent(out) :: ch
integer :: getchar
integer(kind=c_int) :: ich
   ich=system_getchar()
   if(ich.ge.0)then
      ch=char(ich)
   else
      ch=char(0)
   endif
   getchar=ich
end function getchar
